        $result = $this->defaultExtractor->extract($html);
        $this->assertTrue($result['success']);
        $this->assertGreaterThan(0, count($result['title']));
        $this->assertGreaterThan(0, count($result['body']));
    }

    public function testExtractEmpty()
    {
        $result = $this->defaultExtractor->extract('');
        $this->assertFalse($result['success']);
        $this->assertEquals('Unable to fetch content', $result['body']);
    }

    public function testExtractFromUrlSimple()
    {
        $result = $this->articleExtractor->extractFromRemote('https://github.com/laravel/laravel');
        $this->assertTrue($result['success']);
        $this->assertGreaterThan(0, count($result['title']));
        $this->assertGreaterThan(0, count($result['body']));
    }

    public function testExtractFromUrlBadUrl()
    {
        $result = $this->articleExtractor->extractFromRemote('');
        $this->assertFalse($result['success']);
    }

    public function testExtractFromUrlBadUrl404()
    {
        $result = $this->articleExtractor->extractFromRemote('https://github.com/DUMMYDUMMY/DUMMYDUMMY');
        $this->assertFalse($result['success']);
    }
